INSERT INTO `laboratory_page` (`id`, `title`, `slug`, `content`, `banner`, `status`, `created_at`, `created_by`, `last_updated_at`, `last_updated_by`, `meta_title`, `meta_description`, `meta_keywords`) VALUES
(1, 'Laboratory', 'laboratory', '\r\n&#x3C;div class=&#x22;about-page&#x22;&#x3E;\r\n  &#x3C;div class=&#x22;container&#x22;&#x3E;\r\n    &#x3C;div class=&#x22;row&#x22;&#x3E;\r\n      &#x3C;div class=&#x22;col-md-12&#x22;&#x3E;\r\n        &#x3C;div class=&#x22;ab-sec1 ab-sec-ser1&#x22;&#x3E;\r\n          &#x3C;h2&#x3E;Leading the Charge for Environmental Protection With The Best Air Testing Laboratory in India &#x3C;/h2&#x3E;\r\n          &#x3C;p&#x3E;GRC India Training &#x26;amp; Analytical Laboratory is an integral unit of Grass Roots Research &#x26;amp; Creation India (P) Ltd, and is used for utilization of scientific techniques to facilitate protection of the environment. It is recognised by MoEF&#x26;amp;CC and also accredited by NABL. The Laboratory is committed to render reliable services to its customers in accordance with National/ International, customer specific standards and good professional practices with continual improvement in system and services. As a &#x3C;b&#x3E;NABL accredited laboratory in India&#x3C;/b&#x3E;, we extend the finest quality lab services for physical, chemical and microbiological testing. The laboratory has sophisticated instruments like ICP-OES, GC-MS, NDIR CO Monitor, Automatic Weather Monitoring Stations, AAS, GC, Noise Level and Wave Length Monitors and all other requisite instruments and software.&#x3C;/p&#x3E;\r\n        &#x3C;/div&#x3E;\r\n      &#x3C;/div&#x3E;\r\n    &#x3C;/div&#x3E;\r\n    &#x3C;div class=&#x22;row&#x22;&#x3E;\r\n      &#x3C;div class=&#x22;col-md-12&#x22;&#x3E;\r\n        &#x3C;div class=&#x22;lab-pol&#x22;&#x3E;\r\n          &#x3C;div class=&#x22;lab-pol-sec&#x22;&#x3E;\r\n            &#x3C;div class=&#x22;lab-pol-in&#x22;&#x3E;&#x3C;a href=&#x22;#air-sec&#x22;&#x3E;&#x3C;img src=&#x22;https://api.grc-india.com/uploads/images/air.jpg&#x22;&#x3E;&#x3C;span&#x3E;Air&#x3C;/span&#x3E;&#x3C;/a&#x3E;&#x3C;/div&#x3E;\r\n            &#x3C;div class=&#x22;lab-pol-in&#x22;&#x3E;&#x3C;a href=&#x22;#&#x22;&#x3E;&#x3C;img src=&#x22;https://api.grc-india.com/uploads/images/water.jpg&#x22;&#x3E;&#x3C;span&#x3E;Water&#x3C;/span&#x3E;&#x3C;/a&#x3E;&#x3C;/div&#x3E;\r\n            &#x3C;div class=&#x22;lab-pol-in&#x22;&#x3E;&#x3C;a href=&#x22;#&#x22;&#x3E;&#x3C;img src=&#x22;https://api.grc-india.com/uploads/images/soil.jpg&#x22;&#x3E;&#x3C;span&#x3E;Soil&#x3C;/span&#x3E;&#x3C;/a&#x3E;&#x3C;/div&#x3E;\r\n            &#x3C;div class=&#x22;lab-pol-in&#x22;&#x3E;&#x3C;a href=&#x22;#&#x22;&#x3E;&#x3C;img src=&#x22;https://api.grc-india.com/uploads/images/noise.jpg&#x22;&#x3E;&#x3C;span&#x3E;Noise&#x3C;/span&#x3E;&#x3C;/a&#x3E;&#x3C;/div&#x3E;\r\n            &#x3C;div class=&#x22;lab-pol-in&#x22;&#x3E;&#x3C;a href=&#x22;#&#x22;&#x3E;&#x3C;img style=&#x22;height:200px;&#x22; src=&#x22;https://api.grc-india.com/uploads/1684126915920-554505353.jpg&#x22;&#x3E;&#x3C;span&#x3E;Stack&#x3C;/span&#x3E;&#x3C;/a&#x3E;&#x3C;/div&#x3E;\r\n          \r\n        &#x3C;/div&#x3E;\r\n      &#x3C;/div&#x3E;\r\n    &#x3C;/div&#x3E;\r\n  &#x3C;/div&#x3E;\r\n    &#x3C;div class=&#x22;row&#x22;&#x3E;\r\n      &#x3C;div class=&#x22;col-md-6&#x22;&#x3E;\r\n        &#x3C;div class=&#x22;about-img&#x22;&#x3E;\r\n          &#x3C;img src=&#x22;https://api.grc-india.com/uploads/1684230133184-945060707.jpg&#x22; style=&#x22;width: 100%;&#x22;&#x3E;\r\n        &#x3C;/div&#x3E;\r\n      &#x3C;/div&#x3E;\r\n      &#x3C;div class=&#x22;col-md-6&#x22;&#x3E;\r\n      &#x3C;div class=&#x22;about-inner&#x22;&#x3E;\r\n        &#x3C;p&#x3E;GRC India Training &#x26;amp; Analytical Laboratory is dedicated to performing all types of tests/analytical activities, in accordance with the requirements conforming to National and International Standards, meeting the needs of professionals/ interested bodies for carrying out EIA as well as customer specific studies. It provides complete analytical solutions using the expertise of trained and experienced professionals. It is a proficient environmental testing laboratory. It takes pride in offering services from the &#x3C;b&#x3E;best soil testing laboratory in India&#x3C;/b&#x3E;, scientific-minded and passionate environmentalists to comprehend the problem and mitigate the environmental changes. The laboratory also offers training facilities for capacity building of young Scientists / Engineers who have decided to pursue their career in the field of Environment.&#x3C;/p&#x3E;\r\n        &#x3C;/div&#x3E;\r\n      &#x3C;/div&#x3E;  \r\n    &#x3C;/div&#x3E;\r\n    &#x3C;div class=&#x22;row my-5&#x22;&#x3E;\r\n      &#x3C;div class=&#x22;col-md-6&#x22;&#x3E;\r\n      &#x3C;div class=&#x22;about-inner&#x22;&#x3E;\r\n        &#x3C;h3&#x3E;Laboratory Services&#x3C;/h3&#x3E;\r\n        &#x3C;p&#x3E;Compliance executives at GRC India extend services to file your &#x3C;a href=&#x22;https://www.grc-india.com/services/noc-pollution-control-board&#x22; target=&#x22;_blank&#x22;&#x3E;NOC from the Pollution Control Board&#x3C;/a&#x3E; in Delhi NCR. We assist manufacturers to secure their NOC under the Water (Prevention &#x26;amp; Control of Pollution) Act, 1974 and Air (Prevention &#x26;amp; Control of Pollution) Act, 1981.&#x3C;/p&#x3E;\r\n        &#x3C;p&#x3E;Based on the revised classification of industries by the Central Pollution Control Board as of February 2, 2019, GRC India assists in securing NOC to the below-listed industries-&#x3C;/p&#x3E;\r\n        &#x3C;ul&#x3E;\r\n          &#x3C;li&#x3E;Environmental Monitoring&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Analysis of Ground, Surface and Waste Water&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Meteorological Monitoring (Wind speed, Wind direction, Ambient temperature, Relative humidity, Rainfall &#x26;amp; Cloud cover)&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Soil Analysis &#x26;amp; Testing&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Monitoring of Ambient Air Quality / Work Zone Air Quality / Source Emission / Stack Emission and Fugitive Emission&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Ambient, Work Place, Source, DG Noise Monitoring&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Characterization of Soil and its Fertility&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Microbiological Analysis of Water &#x26;amp; Waste Water&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Facilities of Bioassay of Industrial Effluent&#x3C;/li&#x3E;\r\n          &#x3C;li&#x3E;Bio Monitoring (Zooplankton and Phytoplankton Identification) in Surface Water/ Sea&#x3C;/li&#x3E;\r\n        &#x3C;/ul&#x3E;\r\n        &#x3C;/div&#x3E;\r\n      &#x3C;/div&#x3E;  \r\n      &#x3C;div class=&#x22;col-md-6&#x22;&#x3E;\r\n        &#x3C;div class=&#x22;about-img&#x22;&#x3E;\r\n          &#x3C;img src=&#x22;https://www.grc-india.com/images/img-big.jpg&#x22;&#x3E;\r\n        &#x3C;/div&#x3E;\r\n      &#x3C;/div&#x3E;\r\n    &#x3C;/div&#x3E; \r\n&#x3C;/div&#x3E;\r\n&#x3C;/div&#x3E;\r\n&#x3C;div class=&#x22;about-page1&#x22;&#x3E;\r\n  &#x3C;div class=&#x22;container&#x22;&#x3E;\r\n    &#x3C;div class=&#x22;row&#x22;&#x3E;\r\n      &#x3C;div class=&#x22;col-md-6&#x22;&#x3E;\r\n        &#x3C;div class=&#x22;about-img&#x22;&#x3E;\r\n          &#x3C;img src=&#x22;https://www.grc-india.com/images/comp6.jpg&#x22;&#x3E;\r\n        &#x3C;/div&#x3E;\r\n      &#x3C;/div&#x3E;\r\n      &#x3C;div class=&#x22;col-md-6&#x22;&#x3E;\r\n        &#x3C;div class=&#x22;about-inner&#x22;&#x3E;\r\n        &#x3C;h3&#x3E;Get Accurate results for All Analytical Tests/ Activities With GRC India&#x3C;/h3&#x3E;\r\n        &#x3C;p&#x3E;GRC India Training &#x26;amp; Analytical Laboratory is a unit of GRC India. It is recognised by the Ministry of Environment, Forests &#x26;amp; Climate Change, Government of India under The Environment (Protection) Act 1986 and is an ISO 9001:2015, 14001:2015 &#x26;amp; ISO 45001:2018 certified laboratory. GRC India&#x2019;s Laboratory is extensively used for conducting Environmental testing &#x26;amp; Compliance monitoring, covering ambient air, water, soil and noise level analysis.&#x3C;/p&#x3E;\r\n        &#x3C;p&#x3E;Our laboratory is recognised as the best soil testing laboratory in the country and is efficient in delivering optimal results for all analytical tests. As an efficient &#x3C;b&#x3E;air testing laboratory in India&#x3C;/b&#x3E;, we provide testing solutions to grapple with the problems of toxic air quality. At GRC India Training &#x26;amp; Analytical Laboratory, customers get reliable air monitoring services by comprehensive testing for an accurate air quality index. Contact us to know more about our lab testing services for your projects.&#x3C;/p&#x3E;&#x3C;/div&#x3E;&#x3C;/div&#x3E;&#x3C;/div&#x3E;&#x3C;/div&#x3E;&#x3C;/div&#x3E;\r\n\r\n', '1647924711304-885954517.jpg', 'active', '2022-03-21 16:11:03', NULL, '2024-03-11 12:06:58', 1, 'Environmental testing laboratory | NABL accredited laboratory - GRC India', 'Discover the top NABL accredited laboratory - GRC India, to ensure accurate results with cutting-edge technology. Trust our environmental testing laboratory experts for your environmental analysis needs.', 'Environmental testing laboratory India, nabl accredited laboratory in india, Air Testing Laboratory India, Best Soil Testing Laboratory india');
