			SET @queryTotalString = CONCAT('SELECT count(ue.id) as total
											FROM clientele_projects AS ue
											WHERE ue.clientele_item_id= ',clientId);
			SET @queryString = CONCAT('SELECT ue.`id`, 
												ue.`clientele_item_id`, 
                                                ue.`sector_id`,
                                                ue.`short_content`,
                                                ue.`content`,
                                                ue.`is_case_study_available`, 
