                                        LEFT JOIN project_types AS proType ON ue.project_type_id = proType.id
                                        WHERE ue.clientele_item_id= ',clientId);
             IF `pageStatus` IS NOT NULL OR LENGTH(`pageStatus`)>0 THEN
				SET @queryTotalString = CONCAT(@queryTotalString,' AND ue.status = "',pageStatus,'"');
                SET @queryString = CONCAT(@queryString,' AND ue.status = "',pageStatus,'"');
            END IF;
            SET @startFrom = (pageNo-1)*itemPerPage;
            PREPARE stmt FROM @queryTotalString;
			EXECUTE stmt;
			DEALLOCATE PREPARE stmt; 
            IF orderBy IS NOT NULL OR LENGTH(orderBy)>0 THEN
				IF orderBy = 'title' THEN
					SET @queryString = CONCAT(@queryString,' ORDER BY pro.title');
				ELSE
					SET @queryString = CONCAT(@queryString,' ORDER BY ',orderBy);
                END IF;
			ELSE
				SET @queryString = CONCAT(@queryString,' ORDER BY pro.title');
            END IF;
            IF `order` IS NOT NULL OR LENGTH(`order`)>0 THEN
				SET @queryString = CONCAT(@queryString,' ',`order`);
			ELSE 
				SET @queryString = CONCAT(@queryString,' DESC');
            END IF;
            SET @queryString = CONCAT(@queryString,' LIMIT ',itemPerPage,' OFFSET ',@startFrom);
			PREPARE stmt FROM @queryString;
			EXECUTE stmt;
			DEALLOCATE PREPARE stmt; 
            LEAVE this;
		 END$$

